import React, { FormEvent, useState } from 'react';
import { FaWhatsapp } from 'react-icons/fa';
import ReactWhatsapp from 'react-whatsapp';
import './principal.css';

function Principal() {
  var date = new Date();
  const horaAtual = date.getHours();

  function handleSubmit(event: FormEvent) {
    event.preventDefault(); //prevene o funcionamento padrão do usuário
  }

  const [tel, setTel] = useState('');
  return (

    <form onSubmit={handleSubmit} className="teste">
      <div className="input-block">
        <label htmlFor="tel">Telefone para contato</label>
        <textarea
          id="tel"
          value={tel}
          onChange={event => setTel(event.target.value)}
        />
      </div>
      <div className="wppSymbol">
        <FaWhatsapp size={20} color="#FFF" />
        Entrar em contato
      <div className="buttonWpp">
          <ReactWhatsapp
            element="button"
            number="55 31988592881"
            message={
              (horaAtual > 11 && horaAtual < 18) ? 'Boa tarde, Vava' :
                (horaAtual > 17 && horaAtual < 25) ? 'Boa noite, Vava' :
                  'Bom dia, Vava'
            }
          />
        </div>
      </div>
    </form>

  );
}

export default Principal;
