import React from 'react';
import { FaWhatsapp } from 'react-icons/fa';
import  ReactWhatsapp  from 'react-whatsapp';
import './principal.css';

function Principal() {
  var date = new Date();
  const horaAtual = date.getHours();

  return (
    <button type="button" className="contact-button">
      <FaWhatsapp size={20} color="#FFF" />
        Entrar em contato
        {/* <ReactWhatsapp number="55 31988592881" message={`Agora sÃ£o ${horaAtual} VAVA!`}/> */}
        <ReactWhatsapp 
        number="55 31988592881" 
        message={
          (horaAtual > 11 && horaAtual < 18) ? 'Boa tarde, Vava' :
          (horaAtual > 17 && horaAtual < 25) ?'Bom noite, Vava' :
          'Bom dia, Vava'
        } />
    </button>
  );
}

export default Principal;
